import React from 'react';
import { Image, StyleSheet, Text, View } from 'react-native';
import AppLoading from 'expo-app-loading';
import { useFonts, Inter_400Regular } from '@expo-google-fonts/inter';
import { Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/MaterialIcons';
import Buttons from './Buttons';

const Rating = ({ navigation, route }) => {
    let [fontsLoaded] = useFonts({
        Inter_400Regular,
    });

    if (!fontsLoaded) {
        return <AppLoading />;
    }
    const gameParams = route;
    const currentPage = "Rating";
    const nextRoute = "Results";
    const [activeOption, setActiveOption] = useState()
    const updateActiveOption = (option) => {
        setActiveOption(option)
        gameState[currentPage] = option;
    }

    return (
        <View style={styles.container}>
            <Text style={styles.content}>
                Minimum <Text style={{ fontStyle: 'italic' }}>Rating</Text> of the board game?
            </Text>


            <Buttons
                gameParams={gameParams}
                currentPage={currentPage}
                options={options}
                nextRoute={nextRoute}
                navigation={navigation}
            />

            <TouchableOpacity
            style={{ backgroundColor: activeOption === option ? "#FF6767" : 'white'}}
            key={index}
            onPress={() => updateActiveOption(1)} >
                <Text
                style={{
                    width: 75,
                    height: 80,
                    paddingTop: 20,
                    marginRight: 20,
                    marginLeft: 20,
                    marginTop: 20,
                    borderWidth: 1,
                    textAlign: 'center',
                    alignContent: 'center',
                    borderColor: "#FF6767",
                    fontSize: 25,
                    color: activeOption === option ? 'white' : "#FF6767",
                    
                }}>
                    1
                </Text>
            </TouchableOpacity>
            <TouchableOpacity
            style={{ backgroundColor: activeOption === option ? "#FF6767" : 'white'}}
            key={index}
            onPress={() => updateActiveOption(2)} >
                <Text
                style={{
                    width: 75,
                    height: 80,
                    paddingTop: 20,
                    marginRight: 20,
                    marginLeft: 20,
                    marginTop: 20,
                    borderWidth: 1,
                    textAlign: 'center',
                    alignContent: 'center',
                    borderColor: "#FF6767",
                    fontSize: 25,
                    color: activeOption === option ? 'white' : "#FF6767",
                    
                }}>
                    2
                </Text>
            </TouchableOpacity>
            <TouchableOpacity
            style={{ backgroundColor: activeOption === option ? "#FF6767" : 'white'}}
            key={index}
            onPress={() => updateActiveOption(3)} >
                <Text
                style={{
                    width: 75,
                    height: 80,
                    paddingTop: 20,
                    marginRight: 20,
                    marginLeft: 20,
                    marginTop: 20,
                    borderWidth: 1,
                    textAlign: 'center',
                    alignContent: 'center',
                    borderColor: "#FF6767",
                    fontSize: 25,
                    color: activeOption === option ? 'white' : "#FF6767",
                    
                }}>
                    3
                </Text>
            </TouchableOpacity>
            <TouchableOpacity
            style={{ backgroundColor: activeOption === option ? "#FF6767" : 'white'}}
            key={index}
            onPress={() => updateActiveOption(4)} >
                <Text
                style={{
                    width: 75,
                    height: 80,
                    paddingTop: 20,
                    marginRight: 20,
                    marginLeft: 20,
                    marginTop: 20,
                    borderWidth: 1,
                    textAlign: 'center',
                    alignContent: 'center',
                    borderColor: "#FF6767",
                    fontSize: 25,
                    color: activeOption === option ? 'white' : "#FF6767",
                    
                }}>
                    4
                </Text>
            </TouchableOpacity>
            <TouchableOpacity
            style={{ backgroundColor: activeOption === option ? "#FF6767" : 'white'}}
            key={index}
            onPress={() => updateActiveOption(5)} >
                <Text
                style={{
                    width: 75,
                    height: 80,
                    paddingTop: 20,
                    marginRight: 20,
                    marginLeft: 20,
                    marginTop: 20,
                    borderWidth: 1,
                    textAlign: 'center',
                    alignContent: 'center',
                    borderColor: "#FF6767",
                    fontSize: 25,
                    color: activeOption === option ? 'white' : "#FF6767",
                    
                }}>
                    5
                </Text>
            </TouchableOpacity>
            {activeOption ? 
                <Button
                    buttonStyle={styles.navButton}
                    titleStyle={styles.navTitle}
                    onPress={() => navigation.navigate(route, gameState)}
                    icon={
                        <Icon
                            name="navigate-next"
                            size={25}
                            color="white"
                        />
                    }
                    iconRight
                    title="Next"
                />
                :
                null }
            <View style={styles.navRow}>
                <Button
                    buttonStyle={styles.navButton}
                    titleStyle={styles.navTitle}
                    onPress={() => navigation.goBack()}
                    icon={
                        <Icon
                            name="navigate-before"
                            size={25}
                            color="white"
                        />
                    }
                    iconLeft
                    title="Back"
                />

            </View>
        </View>
    );
}

export default Rating;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },

    content: {
        color: "#FF6767",
        fontSize: 25,
        width: 300,
        textAlign: 'center',
        fontFamily: "Inter_400Regular",
    },

    logo: {
        marginBottom: 20,
    },

    buttonRow: {
        marginBottom: 30,
        flexDirection: 'row',
        color: '#fff',
        alignItems: 'center',
        justifyContent: 'space-evenly',

    },

    navRow: {
        marginTop: 30,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
    },

    buttonStyle: {
        width: 80,
        height: 80,
        marginLeft: 15,
        marginRight: 15,
        backgroundColor: '#fff',
        justifyContent: 'center',
        alignItems: 'center',
        borderColor: "#FF6767",
        borderWidth: 2,
    },

    titleStyle: {
        fontFamily: "Inter_400Regular",
        color: "#FF6767",
        fontSize: 25,
    },

    navButton: {
        backgroundColor: "#FF6767",
        justifyContent: 'center',
        alignItems: 'center',
        marginLeft: 55,
        marginRight: 55,
        borderRadius: 60,
        width: 100,
        height: 45,
    },

    navTitle: {
        fontFamily: "Inter_400Regular",
    }
});
